let getTaskTemplate=t=>`
  <li>
    <input type="checkbox" class="task-checkbox" id="${t}">
    <label for="${t}"></label>
    <span class="task-text">${t}</span>
    <button class="delete-task">❌</button>
  </li>
`,addTask=t=>{t=getTaskTemplate(t);$("#tasks-list").append(t)};function ajaxPromise(e,s){return new Promise((t,a)=>{$.ajax({...s,url:e,success:t,error:a})})}$("#tasks-list").on("click",".delete-task",function(){$(this).closest("li").remove()}),$(".form-todo").on("submit",t=>{t.preventDefault();t=$("#task").val().trim();addTask(t),$("#task").val("")});let fetchTasks=async()=>{$(".spinner-border").show();try{var t=(await ajaxPromise("https://jsonplaceholder.typicode.com/todos",{data:{_limit:9}})).map(t=>getTaskTemplate(t.title,t.completed)).join("");$("#tasks-list").html(t),$(".spinner-border").hide()}catch(t){alert("Failed to load tasks. Please try again!")}};fetchTasks();