let getTaskTemplate=t=>`
  <li>
    <input type="checkbox" class="task-checkbox" id="${t}">
    <label for="${t}"></label>
    <span class="task-text">${t}</span>
    <button class="delete-task">❌</button>
  </li>
`,addTask=t=>{t=getTaskTemplate(t);$("#tasks-list").append(t)},fetchTasks=($("#tasks-list").on("click",".delete-task",function(){$(this).closest("li").remove()}),$("#add").on("click",function(){var t=$("#task").val().trim();t&&(addTask(t),$("#task").val(""))}),$(".input-container").on("keydown",function(t){13===t.which&&addTask()}),async()=>{var t=(await(await fetch("https://jsonplaceholder.typicode.com/todos?_limit=9")).json()).map(t=>getTaskTemplate(t.title)).join("");$("#tasks-list").html(t)});fetchTasks();